$(document).ready(()=>{const bar=$('.fa-bars');const navbar=$('.navbar');const navMenu=$('.nav-group-items');const primary='#173037';let width=window.outerWidth;window.addEventListener('resize',()=>{width=window.outerWidth;if(width>1337){navMenu.css('display','flex');window.scrollY>100?navbar.css('background',primary):navbar.css('background','transparent')}else{navMenu.css('display','none');navbar.css('background',primary)}});window.addEventListener('scroll',()=>{if(width>1337)
  window.scrollY>100?navbar.css('background',primary):navbar.css('background','transparent');else navbar.css('background',primary)});bar.click((e)=>{e.preventDefault();width<=1337?navMenu.slideToggle():navMenu.css('display','flex')})})